CREATE TABLE `ltb_web`.`tasks` ( `id` INT(11) NOT NULL AUTO_INCREMENT , `name` VARCHAR(255) NOT NULL , `text` TEXT NOT NULL , `email` VARCHAR(255) NOT NULL , PRIMARY KEY (`id`)) ENGINE = MyISAM COMMENT = 'Таблица хранит список задач и статусы их выполнения';
ALTER TABLE `tasks` ADD `status` ENUM('closed','changed') NULL DEFAULT NULL AFTER `email`;
ALTER TABLE `tasks` CHANGE `id` `id` INT(11) NOT NULL AUTO_INCREMENT, CHANGE `name` `name` VARCHAR(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL, CHANGE `text` `text` TEXT CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL, CHANGE `email` `email` VARCHAR(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL, CHANGE `status` `status` ENUM('closed','changed') CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL;